var tabsWrap=document.querySelector(".calculate__type-tabs-wrap");tabsWrap.addEventListener("click",(function(t){if(t.target.classList.contains("calculate__tab")){for(i=0;i<tabsWrap.children.length;i++)tabsWrap.children[i].classList.remove("calculate__tab_active");t.target.classList.add("calculate__tab_active")}})),$("input[type=range]").on("input",(function(t){var a=t.target.min,e=t.target.max,c=t.target.value;$(t.target).css({backgroundSize:100*(c-a)/(e-a)+"% 100%"})})).trigger("input");